import {
  Button,
  Card,
  Form,
  FormLayout,
  Layout,
  Page,
  SettingToggle,
  Stack,
  TextField,
  TextStyle,
  DataTable,
  Heading,
  Badge
} from '@shopify/polaris';


class FreeShippingBar extends React.Component {
  state = {
      bars: [
          { id: 1, name: 'My first shipping bar 1' },
          { id: 2, name: 'My first shipping bar 2' },
      ],
      name: 'My first free shipping bar',
      goal: '100',
  };
    renderDashboardTableData() {
      return this.state.bars.map((bar, index) => {
        const { id, name } = bar
        return (
            <tr class="Polaris-DataTable__TableRow" key={id}>
              <td class="Polaris-DataTable__Cell Polaris-DataTable__Cell--verticalAlignTop">
                <Stack>
                  <Badge status="success">Active</Badge>
                </Stack>
                {name}
              </td>
              <td class="Polaris-DataTable__Cell Polaris-DataTable__Cell--verticalAlignTop">
                <Stack>
                  <Button primary>Edit</Button>
                  <Button>Duplicate</Button>
                  <Button>Activate</Button>
                </Stack>
                </td>
            </tr>
        )
      })
  }
  render() {
    const { name, goal } = this.state;

    return (
        <Page>
          <Layout>
          <Layout.Section>
            <Card sectioned>
              <Stack>
                <Stack.Item fill>
                  <Heading>Free Shipping Bar (FSB) Dashboard</Heading>
                </Stack.Item>
                <Stack.Item>
                  <Button primary>Create New Bar</Button>
                </Stack.Item>
              </Stack>
              <div class="Polaris-DataTable">
                <div class="Polaris-DataTable__ScrollContainer">
                  <table id='bars' class="Polaris-DataTable__Table"> 
                    <thead>
                      <tr>
                        <th class="Polaris-DataTable__Cell Polaris-DataTable__Cell--verticalAlignTop Polaris-DataTable__Cell--firstColumn Polaris-DataTable__Cell--header">Name</th>
                        <th class="Polaris-DataTable__Cell Polaris-DataTable__Cell--verticalAlignTop Polaris-DataTable__Cell--firstColumn Polaris-DataTable__Cell--header">Actions</th>
                      </tr>
                    </thead>
                    <tbody>
                        {this.renderDashboardTableData()}
                    </tbody>
                  </table>
                </div>
              </div>
            </Card>
          </Layout.Section>
        </Layout>
        <Layout>
          <Layout.Section
          >
            <Form onSubmit={this.handleSubmit}>
              <Card title="Templates" sectioned>
                  <FormLayout>
                    <TextField
                      value={name}
                      onChange={this.handleChange('name')}
                      label="Name"
                      type="name"
                    />
                    <TextField
                      value={goal}
                      onChange={this.handleChange('goal')}
                      label="Free Shipping Goal"
                      type="goal"
                    />
                    <Stack distribution="trailing">
                      <Button primary submit>
                        Save
                      </Button>
                    </Stack>
                  </FormLayout>
              </Card>

            </Form>
          </Layout.Section>
        </Layout>
      </Page>
    );
  }
  handleSubmit = () => {
    this.setState({
      name: this.state.name,
      goal: this.state.goal
    });
    console.log('submission', this.state);
  };
  handleChange = (field) => {
    return (value) => this.setState({[field]: value});
  };
}
export default FreeShippingBar;
