import {
  Button,
  Card,
  Form,
  FormLayout,
  Layout,
  Page,
  SettingToggle,
  Stack,
  TextField,
  TextStyle,
  DataTable,
} from '@shopify/polaris';


class FreeShippingBar extends React.Component {
  state = {
      students: [
          { id: 1, name: 'My first shipping bar 1' },
          { id: 2, name: 'My first shipping bar 2' },
      ],
      name: 'My first free shipping bar',
      goal: '100',
  };
    renderTableData() {
      return this.state.students.map((student, index) => {
        const { id, name, age, email } = student //destructuring
        return (
            <tr key={id}>
              <td>{id}</td>
              <td>{name}</td>
              <td>{age}</td>
              <td>{email}</td>
            </tr>
        )
      })
  }
  render() {
    const { name, goal } = this.state;

    return (
        <Page>
          <Layout>
          <Layout.Section>
            <Card title="Free Shipping Bar (FSB) Dashboard" sectioned>
              <DataTable
                columnContentTypes={[
                  'text',
                  'text'
                ]}
                headings={[
                  'Name',
                  'Actions',
                ]}
                rows={rows}
              />
            </Card>
          </Layout.Section>
        </Layout>
        <Layout>
          <Layout.AnnotatedSection
            title="Content Configuration"
            description="This is a content configuration panel"
          >
            <Card sectioned>
              <Form onSubmit={this.handleSubmit}>
                <FormLayout>
                  <TextField
                    value={name}
                    onChange={this.handleChange('name')}
                    label="Name"
                    type="name"
                  />
                  <TextField
                    value={goal}
                    onChange={this.handleChange('goal')}
                    label="Free Shipping Goal"
                    type="goal"
                  />
                  <Stack distribution="trailing">
                    <Button primary submit>
                      Save
                    </Button>
                  </Stack>
                </FormLayout>
              </Form>
            </Card>
          </Layout.AnnotatedSection>
        </Layout>
      </Page>
    );
  }
  handleSubmit = () => {
    this.setState({
      name: this.state.name,
      goal: this.state.goal
    });
    console.log('submission', this.state);
  };
  handleChange = (field) => {
    return (value) => this.setState({[field]: value});
  };
}
export default FreeShippingBar;
